mapper.insert:
insert into BOARDS (
	boardnum
	, title
	, idno
	, content
	, regdate
	, moddate
	, hitcount
	, subject
)
values (
	boards_seq.nextval
	, #{title}
	, #{idno}
	, #{content}
	, sysdate
	, sysdate
	, #{hitcount}
	, #{subject}
	)

mapper.delete:
delete
from
	boards
where
	boardnum = #{boardnum}
	and idno = #{idno}

mapper.update:
update
	boards
set
	title = #{title}
	, content = #{content}
where
	boardnum = #{boardnum}
	and idno = #{idno}

mapper.insert:
insert into replies (
	replynum
	, boardnum
	, idno
	, content
	, stars
)
values (
	replies_seq.nextval
	, #{boardnum}
	, #{idno}
	, #{content}
	, #{stars}
)

mapper.delete:
delete
from
	replies
where
	replynum = #{replynum}
	and idno = #{idno}

mapper.update:
update
	replies
set
	content = #{content}
	,stars  = #{stars}
where
	replynum = #{replynum}
	and idno = #{idno}
