mapper.insert:
insert into articles (
	articleid
	, articlename
	, articlecontent
	, articledate
	, presscompany
	, area
	, url
	, continent
)
values (
	#{articleid}
	, #{articlename}
	, #{articlecontent}
	, TO_DATE(#{articledate}, 'yyyy/mm/dd')
	, #{presscompany}
	, #{area}
	, #{url}
	, #{continent, jdbcType=VARCHAR}
)

	insert all

        into articles (
			articleid
			, articlename
			, articlecontent
			, articledate
			, presscompany
			, area
			, url
			, continent
		)
		values (
			#{element.articleid}
			, #{element.articlename}
			, #{element.articlecontent}
			, TO_DATE(#{element.articledate}, 'yyyy/mm/dd')
			, #{element.presscompany}
			, #{element.area}
			, #{element.url}
			, #{element.continent}
		) 

	select * from dual
