mapper.insert:
insert into articles (
	articlenum
	, articleid
	, articlename
	, articlecontent
	, articledate
	, presscompany
	, url

	, continent

)
values (
	articlenum.nextVal
	, #{articleid}
	, #{articlename}
	, #{articlecontent}
	, TO_DATE(#{articledate}, 'YYYY/MM/DD')
	, #{presscompany}
	, #{url}

	, #{continent, jdbcType=VARCHAR}

)

insert into article_html (
	articleid
	, articlehtml
)
values (
	#{articleid}
	, #{articlehtml}
)

	insert all

        into articles (
			articleid
			, articlename
			, articlecontent
			, articledate
			, presscompany
			, url
			, continent
		)
		values (
			#{element.articleid}
			, #{element.articlename}
			, #{element.articlecontent}
			, TO_DATE(#{element.articledate}, 'YYYY/MM/DD')
			, #{element.presscompany}
			, #{element.url}
			, #{element.continent, jdbcType=VARCHAR}
		) 

	select * from dual
